// @flow
import * as React from 'react';
import { graphql, QueryProps } from 'react-apollo';
import gql from 'graphql-tag';

// import types generated by codegen
import { GetCharacterQuery, GetCharacterQueryVariables } from './schema';

const HERO_QUERY = gql`
  query GetCharacter($episode: Episode!) {
    hero(episode: $episode) {
      name
      id
      friends {
        name
        id
        appearsIn
      }
    }
  }
`;

export type ShapedProps = GetCharacterQuery & QueryProps;

const withCharacter = graphql<
  GetCharacterQuery,
  GetCharacterQueryVariables,
  ShapedProps
>(HERO_QUERY, {
  options: ({ episode }) => ({
    variables: { episode },
  }),
  props: ({ data }) => ({ ...data }),
});

export const Character = withCharacter(({ loading, hero, error }) => {
  if (loading) return <div>Loading</div>;
  if (error) return <h1>ERROR</h1>;
  return (
    <div>
      {hero &&
        <div>
          <h3>
            {hero.name}
          </h3>
          {hero.friends &&
            hero.friends.map(
              friend =>
                friend &&
                <h6 key={friend.id}>
                  {friend.name}:{' '}
                  {friend.appearsIn.map(x => x && x.toLowerCase()).join(', ')}
                </h6>,
            )}
        </div>}
    </div>
  );
});

export const App = () =>
  <div>
    <Character episode="NEWHOPE" />
  </div>;
